function UserBiddingVerification() {
    // Get the response object
    var resultObj = voltMX.getResultObject();
    
    var resultJson = resultObj.toJson();
    var result = resultJson.records[0];
   

    if(resultJson.records.length > 0){
       
        resultObj.params['total_bidded_amount']=result.user_vehcile_total_bidded_amount;

        var credit_limit_int = parseInt(result.credit_limit);
        var bidding_deposite_value_int = parseInt(result.bidding_deposite_value)
        var user_vehcile_total_bidded_amount_int = parseInt(result.user_vehcile_total_bidded_amount)
        if(credit_limit_int >= user_vehcile_total_bidded_amount_int ){
            resultObj.params['condition_message']="Success";
            resultObj.params['message']="Success";
        }
        else{
            resultObj.params['condition_message']="Fail";
            resultObj.params['message']="Fail";
        }
    }
    else{
        resultObj.params['condition_message']="Fail";
        resultObj.params['message']="Fail";
    }

    resultObj.update();
}

// Execute the function
UserBiddingVerification();
